-- d_taiwan.da_channel definition

CREATE TABLE `da_channel`
(
    `id`             bigint(20)   NOT NULL AUTO_INCREMENT COMMENT '编号',
    `pid`            varchar(12) DEFAULT NULL COMMENT 'PID',
    `channel_name`   varchar(100) NOT NULL COMMENT '频道名',
    `frida_client`   varchar(64) DEFAULT NULL COMMENT '客户端',
    `script_context` longtext COMMENT '脚本内容',
    `main_python`    longtext COMMENT 'python脚本',
    `channel_status` tinyint(1)  DEFAULT '0' COMMENT '状态 0离线 1在线',
    `frida_status`   tinyint(1)  DEFAULT '0' COMMENT '状态 0离线 1运行',
    PRIMARY KEY (`id`)
) ENGINE = InnoDB
  AUTO_INCREMENT = 3
  DEFAULT CHARSET = utf8 COMMENT ='频道信息';


-- d_taiwan.da_frida_function definition

CREATE TABLE `da_frida_function`
(
    `id`                bigint(20) NOT NULL AUTO_INCREMENT COMMENT '编号',
    `children_function` varchar(256) DEFAULT NULL COMMENT '依赖函数',
    `function_name`     varchar(32)  DEFAULT NULL COMMENT '函数名',
    `function_context`  longtext     DEFAULT NULL COMMENT '函数内容',
    `remark`            varchar(256) DEFAULT NULL COMMENT '备注',
    PRIMARY KEY (`id`)
) ENGINE = InnoDB
  DEFAULT CHARSET = utf8 COMMENT ='frida函数信息';


-- d_taiwan.da_frida_script definition

CREATE TABLE `da_frida_script`
(
    `id`                bigint(20)   NOT NULL AUTO_INCREMENT COMMENT '编号',
    `script_name`       varchar(100) NOT NULL COMMENT '脚本名',
    `script_context`    longtext COMMENT '脚本内容',
    `children_function` varchar(256) DEFAULT NULL COMMENT '依赖函数',
    `remark`            varchar(255) DEFAULT NULL COMMENT '备注',
    PRIMARY KEY (`id`)
) ENGINE = InnoDB
  DEFAULT CHARSET = utf8 COMMENT ='frida脚本信息';

